// Service Worker para notifica√ß√µes push
const CACHE_NAME = '7care-v1';
const STATIC_ASSETS = [
  '/',
  '/dashboard',
  '/manifest.json',
  '/pwa-192x192.png',
  '/pwa-512x512.png'
];

// Instalar service worker
self.addEventListener('install', (event) => {
  console.log('üîß Service Worker: Instalando...');
  event.waitUntil(
    caches.open(CACHE_NAME)
      .then((cache) => {
        console.log('üì¶ Service Worker: Cache aberto');
        return cache.addAll(STATIC_ASSETS);
      })
      .catch((error) => {
        console.error('‚ùå Service Worker: Erro ao cachear assets:', error);
      })
  );
  // For√ßa ativa√ß√£o imediata
  self.skipWaiting();
});

// Ativar service worker
self.addEventListener('activate', (event) => {
  console.log('‚úÖ Service Worker: Ativando...');
  event.waitUntil(
      caches.keys().then((cacheNames) => {
        return Promise.all(
          cacheNames.map((cacheName) => {
          if (cacheName !== CACHE_NAME) {
            console.log('üóëÔ∏è Service Worker: Removendo cache antigo:', cacheName);
              return caches.delete(cacheName);
            }
          })
        );
    })
  );
  // Toma controle imediato
  self.clients.claim();
});

// Interceptar requisi√ß√µes
self.addEventListener('fetch', (event) => {
  // Filtrar requisi√ß√µes que n√£o devem ser cacheadas
  const url = new URL(event.request.url);
  
  // N√£o cachear APIs, chrome-extensions, ou outros esquemas n√£o suportados
  if (
    event.request.url.includes('/api/') ||
    url.protocol === 'chrome-extension:' ||
    url.protocol === 'chrome:' ||
    url.protocol === 'moz-extension:' ||
    url.protocol === 'edge-extension:' ||
    url.protocol === 'safari-extension:' ||
    url.protocol === 'data:' ||
    url.protocol === 'blob:' ||
    !url.protocol.startsWith('http')
  ) {
    return;
  }

  // S√≥ interceptar requisi√ß√µes do mesmo dom√≠nio
  if (url.origin !== self.location.origin) {
    return;
  }

  event.respondWith(
    caches.match(event.request)
      .then((response) => {
        // Retorna do cache se dispon√≠vel
        if (response) {
          return response;
        }
        
        // Sen√£o, faz a requisi√ß√£o
        return fetch(event.request)
          .then((fetchResponse) => {
            // Verifica se a resposta √© v√°lida e pode ser cacheada
            if (!fetchResponse || 
                fetchResponse.status !== 200 || 
                fetchResponse.type !== 'basic' ||
                !fetchResponse.headers.get('content-type')?.includes('text') ||
                event.request.method !== 'GET') {
              return fetchResponse;
            }

            // Clona a resposta para cache
            const responseToCache = fetchResponse.clone();

            // Adiciona ao cache para uso futuro (com tratamento de erro)
            caches.open(CACHE_NAME)
              .then((cache) => {
                try {
                  cache.put(event.request, responseToCache);
                } catch (error) {
                  console.warn('‚ö†Ô∏è Service Worker: Erro ao salvar no cache:', error);
                }
              })
              .catch((error) => {
                console.warn('‚ö†Ô∏è Service Worker: Erro ao abrir cache:', error);
              });

            return fetchResponse;
          })
          .catch((error) => {
            console.error('‚ùå Service Worker: Erro na requisi√ß√£o:', error);
            
            // Retorna uma resposta de erro personalizada para p√°ginas
            if (event.request.destination === 'document') {
              return new Response(
                `<!DOCTYPE html>
                <html>
                  <head>
                    <title>Erro de Conex√£o - 7care</title>
                    <meta charset="utf-8">
                    <meta name="viewport" content="width=device-width, initial-scale=1">
                  </head>
                  <body>
                    <h1>Sem conex√£o</h1>
                    <p>Verifique sua internet e tente novamente.</p>
                    <button onclick="window.location.reload()">Tentar Novamente</button>
                  </body>
                </html>`,
                {
                  status: 200,
                  statusText: 'OK',
                  headers: { 'Content-Type': 'text/html' }
                }
              );
            }
            
            // Para outros recursos, re-lan√ßa o erro
            throw error;
          });
      })
      .catch((error) => {
        console.error('‚ùå Service Worker: Erro geral na requisi√ß√£o:', error);
        // Retorna erro para n√£o quebrar a aplica√ß√£o
        return new Response('Erro de rede', { status: 408 });
      })
  );
});

// Push notifications
self.addEventListener('push', (event) => {
  console.log('üì± Service Worker: Push notification recebida');
  
  let data = {};
  if (event.data) {
    try {
      data = event.data.json();
    } catch (e) {
      data = { title: '7care', body: event.data.text() };
    }
  }
  
  const options = {
    title: data.title || '7care',
    body: data.message || data.body || 'Nova notifica√ß√£o',
    icon: '/pwa-192x192.png',
    badge: '/pwa-192x192.png',
    tag: data.type || 'general',
    data: data,
    requireInteraction: data.type === 'urgent',
    actions: [
      {
        action: 'open',
        title: 'Abrir App',
        icon: '/pwa-192x192.png'
      },
      {
        action: 'close',
        title: 'Fechar'
      }
    ]
  };

  event.waitUntil(
    self.registration.showNotification(options.title, options)
  );
});

// Click em notifica√ß√£o
self.addEventListener('notificationclick', (event) => {
  console.log('üëÜ Service Worker: Notifica√ß√£o clicada');
  
  event.notification.close();
    
    if (event.action === 'close') {
      return;
    }
    
  // Abrir o app
    event.waitUntil(
      clients.matchAll({ type: 'window', includeUncontrolled: true })
        .then((clientList) => {
        // Se j√° h√° uma janela aberta, focar nela
        for (const client of clientList) {
            if (client.url.includes(self.location.origin) && 'focus' in client) {
              return client.focus();
            }
          }
        // Sen√£o, abrir nova janela
          if (clients.openWindow) {
          return clients.openWindow('/dashboard');
          }
        })
    );
});

// Background sync (se suportado)
self.addEventListener('sync', (event) => {
  console.log('üîÑ Service Worker: Background sync:', event.tag);
  
  if (event.tag === 'background-sync') {
    event.waitUntil(
      syncOfflineData()
    );
  }
  
  if (event.tag === 'sync-users') {
    event.waitUntil(
      syncSpecificData('users')
    );
  }
  
  if (event.tag === 'sync-tasks') {
    event.waitUntil(
      syncSpecificData('tasks')
    );
  }
  
  if (event.tag === 'sync-calendar') {
    event.waitUntil(
      syncSpecificData('calendar')
    );
  }
});

// Fun√ß√£o para sincronizar dados offline
async function syncOfflineData() {
  try {
    console.log('üîÑ Service Worker: Iniciando sincroniza√ß√£o offline...');
    
    // Verificar se h√° dados para sincronizar
    const offlineData = await getOfflineData();
    
    if (!offlineData || offlineData.length === 0) {
      console.log('üì≠ Service Worker: Nenhum dado offline para sincronizar');
      return;
    }
    
    console.log(`üìä Service Worker: ${offlineData.length} opera√ß√µes offline encontradas`);
    
    let successCount = 0;
    let errorCount = 0;
    
    // Processar cada opera√ß√£o offline
    for (const operation of offlineData) {
      try {
        await processOfflineOperation(operation);
        successCount++;
        console.log(`‚úÖ Service Worker: Opera√ß√£o sincronizada: ${operation.type} ${operation.endpoint}`);
      } catch (error) {
        errorCount++;
        console.error(`‚ùå Service Worker: Erro na sincroniza√ß√£o:`, error);
        
        // Se falhou, atualizar contador de tentativas
        await updateOperationRetryCount(operation.id, error);
      }
    }
    
    console.log(`üìà Service Worker: Sincroniza√ß√£o conclu√≠da: ${successCount} sucessos, ${errorCount} falhas`);
    
    // Notificar o cliente sobre o resultado
    await notifyClients({
      type: 'sync-completed',
      success: successCount,
      errors: errorCount,
      total: offlineData.length
    });
    
  } catch (error) {
    console.error('‚ùå Service Worker: Erro na sincroniza√ß√£o offline:', error);
    
    // Notificar o cliente sobre o erro
    await notifyClients({
      type: 'sync-error',
      error: error.message
    });
  }
}

// Fun√ß√£o para sincronizar dados espec√≠ficos
async function syncSpecificData(type) {
  try {
    console.log(`üîÑ Service Worker: Sincronizando ${type}...`);
    
    const offlineData = await getOfflineDataByType(type);
    
    if (!offlineData || offlineData.length === 0) {
      console.log(`üì≠ Service Worker: Nenhum dado ${type} para sincronizar`);
      return;
    }
    
    for (const operation of offlineData) {
      await processOfflineOperation(operation);
    }
    
    console.log(`‚úÖ Service Worker: ${type} sincronizado com sucesso`);
    
  } catch (error) {
    console.error(`‚ùå Service Worker: Erro na sincroniza√ß√£o de ${type}:`, error);
  }
}

// Fun√ß√£o para processar uma opera√ß√£o offline
async function processOfflineOperation(operation) {
  const options = {
    method: operation.method,
    headers: {
      'Content-Type': 'application/json',
      ...operation.headers
    }
  };
  
  if (operation.data && ['POST', 'PUT'].includes(operation.method)) {
    options.body = JSON.stringify(operation.data);
  }
  
  const response = await fetch(operation.endpoint, options);
  
  if (!response.ok) {
    throw new Error(`HTTP ${response.status}: ${response.statusText}`);
  }
  
  // Remover opera√ß√£o da fila offline ap√≥s sucesso
  await removeOfflineOperation(operation.id);
  
  return response;
}

// Fun√ß√£o para obter dados offline do IndexedDB
async function getOfflineData() {
  return new Promise((resolve, reject) => {
    const request = indexedDB.open('7care-offline-queue', 1);
    
    request.onsuccess = () => {
      const db = request.result;
      const transaction = db.transaction(['queue'], 'readonly');
      const store = transaction.objectStore('queue');
      const getAllRequest = store.getAll();
      
      getAllRequest.onsuccess = () => {
        resolve(getAllRequest.result || []);
      };
      
      getAllRequest.onerror = () => {
        reject(getAllRequest.error);
      };
    };
    
    request.onerror = () => {
      reject(request.error);
    };
  });
}

// Fun√ß√£o para obter dados offline por tipo
async function getOfflineDataByType(type) {
  const allData = await getOfflineData();
  return allData.filter(operation => 
    operation.endpoint.includes(`/${type}`) || 
    operation.metadata?.category === type
  );
}

// Fun√ß√£o para remover opera√ß√£o offline
async function removeOfflineOperation(id) {
  return new Promise((resolve, reject) => {
    const request = indexedDB.open('7care-offline-queue', 1);
    
    request.onsuccess = () => {
      const db = request.result;
      const transaction = db.transaction(['queue'], 'readwrite');
      const store = transaction.objectStore('queue');
      const deleteRequest = store.delete(id);
      
      deleteRequest.onsuccess = () => {
        resolve();
      };
      
      deleteRequest.onerror = () => {
        reject(deleteRequest.error);
      };
    };
    
    request.onerror = () => {
      reject(request.error);
    };
  });
}

// Fun√ß√£o para atualizar contador de tentativas
async function updateOperationRetryCount(id, error) {
  return new Promise((resolve, reject) => {
    const request = indexedDB.open('7care-offline-queue', 1);
    
    request.onsuccess = () => {
      const db = request.result;
      const transaction = db.transaction(['queue'], 'readwrite');
      const store = transaction.objectStore('queue');
      const getRequest = store.get(id);
      
      getRequest.onsuccess = () => {
        const operation = getRequest.result;
        if (operation) {
          operation.retryCount = (operation.retryCount || 0) + 1;
          operation.lastError = error.message;
          operation.lastRetryTime = Date.now();
          
          // Se excedeu o m√°ximo de tentativas, remover
          if (operation.retryCount >= (operation.maxRetries || 3)) {
            store.delete(id);
          } else {
            store.put(operation);
          }
        }
        resolve();
      };
      
      getRequest.onerror = () => {
        reject(getRequest.error);
      };
    };
    
    request.onerror = () => {
      reject(request.error);
    };
  });
}

// Fun√ß√£o para notificar clientes
async function notifyClients(data) {
  try {
    const clients = await self.clients.matchAll();
    clients.forEach(client => {
      client.postMessage(data);
    });
  } catch (error) {
    console.error('‚ùå Service Worker: Erro ao notificar clientes:', error);
  }
}

// Message handling
self.addEventListener('message', (event) => {
  console.log('üí¨ Service Worker: Mensagem recebida:', event.data);

    if (event.data && event.data.type === 'SKIP_WAITING') {
      self.skipWaiting();
  }
});

// Suprimir erros de Chrome Extensions
self.addEventListener('error', (event) => {
  if (event.message && event.message.includes('chrome-extension')) {
    event.preventDefault();
    return;
  }
});

self.addEventListener('unhandledrejection', (event) => {
  if (event.reason && event.reason.message && event.reason.message.includes('chrome-extension')) {
    event.preventDefault();
    return;
  }
});

console.log('üöÄ Service Worker: Carregado e pronto!');
